package multiply;

public class multiply {

	public static int multiply(int a, int b) {
		return a*b;
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 2;
		int b = 3;
		int c = 0;
		c = multiply(a, b);
		System.out.print(a + " x " + b + " = " + c);
	}

}
